<?phpClass URL{	protected static function Encryption($value){		$str = base64_encode($value);		return str_replace('=', '%1yt', $str);	}	/*	*URL::To(['site/index','key'=>'value']);	*/	public static function To($arr=array()){		$url = '';		foreach ($arr as $key => $value) {			$k = $key?'&'.$key:'?r';			$url .= $k.'='.self::Encryption($value);		}		return $url;	}	public static function GOTURL(){		/*isset($_GET['r']) ? $con = $_GET['r'] : $con = 'site/index';		$url_str = ucwords($con);		return explode('/', $url_str);*/		if(isset($_GET['r'])){			$url_str = str_replace('%1yt', '=', $_GET['r']);			$urls = base64_decode($url_str);			return explode('/', ucwords($urls));		}else{			return ['Site','index'];		}	}	public static function unEncryption(){		$url_str = str_replace('%1yt', '=', $_GET['r']);		return base64_decode($url_str);	}    public static function unValues($str){        $data =  str_replace('%1yt', '=', $str);        return base64_decode($data);    }}